import { MatPaginatorIntl } from '@angular/material/paginator';

const espRangeLabel = (page, pageSize, length) => {
  if (length == 0 || pageSize == 0) {
    return `0 de ${length}`;
  }

  length = Math.max(length, 0);
  const startIndex = page * pageSize; // If the start index exceeds the list length, do not try and fix the end index to the end.

  const endIndex = startIndex < length ? Math.min(startIndex + pageSize, length) : startIndex + pageSize;
  return `${startIndex + 1} - ${endIndex} de ${length}`;
};

export function getEspPaginatorIntl() {
  const paginatorIntl = new MatPaginatorIntl();
  paginatorIntl.itemsPerPageLabel = 'Registros por página:';
  paginatorIntl.nextPageLabel = 'Página Siguiente';
  paginatorIntl.previousPageLabel = 'Página Anterior';
  paginatorIntl.firstPageLabel = 'Primer Página';
  paginatorIntl.lastPageLabel = 'Ultima Página';
  paginatorIntl.getRangeLabel = espRangeLabel;
  return paginatorIntl;
}
